import Foundation
import SwiftUI

struct Park {
    let parkName: String
    let parkLocation: String
    let dateEstablished: String
    let visitor: Int
    let parkImage: String
    let parkDescription: String
    static var sortByVisitors = false
    
    static func natParks() -> [Park] {
        
        var parks: [Park] = []
        
        for i in 0..<numIntVisitors.count {
            let names = parkNames[i]
            let locations = parkLocations[i]
            let dates = datesEstablished[i]
            let visitors = Int(exactly: numIntVisitors[i])!
            let images = parkImages[i]
            let descriptions = parkDescriptions[i]
        
            let park = Park(parkName: names, parkLocation: locations, dateEstablished: dates, visitor: visitors, parkImage: images, parkDescription: descriptions)
                            parks.append(park)
        }
   
        if sortByVisitors == true {

            return parks.sorted {$0.visitor > $1.visitor}
            
        } else {
            
            return parks
        }
    }
    
}
    
    
    
    
